function SharedBundleDivider(a){this.dom=$(".shared-bundles-divider");this.start()}SharedBundleDivider.method("hide",function(){this.dom.changeClass("shared-bundles-divider-notification","shared-bundles-divider");$("#collaboration-notifications").hide()});SharedBundleDivider.method("start",function(){var a=this;if(Account.loggedIn()){SubscriptionHandler.pull(Account.id(),Account.key(),function(){var b=SubscriptionHandler.getInvites().length+Object.keys(SubscriptionHandler.updatedBundles).length;if(b>0){a.dom.changeClass("shared-bundles-divider","shared-bundles-divider-notification");$("#collaboration-notifications").addClass("notification-{0}".format(b>5?"5p":b))}})}this.dom.click(function(){a.hide()})});var sharedBundleDivider=new SharedBundleDivider();