function SignUpPage(a,b){this._init("#sign-up-container",".tb-page","#tab-bundler-container",a,b,true);this.$name=$("#sign-up-name");this.$email=$("#sign-up-email");this.$password=$("#sign-up-password");this.$referralCode=$("#referral-code");this.$canContact=$("#can-contact");this.$signUp=$("#sign-up");this.wheel=new Wheel("#sign-up-wheel")}SignUpPage.inherits(Page);SignUpPage.method("toggle",function(a,b){this.uber("_toggle",function(){Mousetrap.pause()},function(){Mousetrap.unpause()})});SignUpPage.method("start",function(){var d=this;var c=[d.$name,d.$email,d.$password,d.$referralCode];for(var b=0;b<c.length;b++){var a=c[b];if(!b==c.length-1){a.onEnter(function(){c[b+1].focus()})}else{a.onEnter(function(){d.$signUp.click()})}}d.$signUp.click(function(){var h=d.$name.val();var g=d.$email.val();var f=d.$password.val();var i=d.$referralCode.val();var e=d.$canContact.prop("checked")?1:0;if(d.wheel.showing){return}d.wheel.start();Account.signUp(h,g,f,e,i,Version.isNewInstall,function(){d.wheel.stop();Popup.show(Messages.SignUpSuccessTitle.format(h[0].toUpperCase()+h.slice(1)),Messages.SignUpSuccess,5000);$(document).trigger("login-page-event")},function(j){d.wheel.stop();$("#tb-sign-up-error").text(j.message)})})});var signUpPage=new SignUpPage(".sign-up-page-toggler","sign-up-page-event");