String.prototype.escapeHTML=function(){return(this.replace(/&/g,"&amp;").replace(/>/g,"&gt;").replace(/</g,"&lt;").replace(/"/g,"&quot;"))};String.prototype.format=function(){var a=arguments;return this.replace(/{(\d+)}/g,function(b,c){return typeof a[c]!="undefined"?a[c]:b})};String.prototype.removeQuery=function(){return this.split("?")[0]};String.prototype.sameUrl=function(a){return this.removeQuery()==a.removeQuery()};String.prototype.htmlEncode=function(){return this.split("&").map(function(b,a){return $("<div/>").text(b).html()}).join("&")};String.prototype.htmlDecode=function(){return $("<div/>").html(this).text()};String.prototype.isNumeric=function(){return(this-0)==this&&(""+this).trim().length>0};